import React, { useState } from "react";

function Size() {
  const [weight, setWeight] = useState("");
  const [height, setHeight] = useState("");
  const [bmiResult, setBmiResult] = useState(null);
  const [status, setStatus] = useState("");
  const [imageSrc, setImageSrc] = useState("");

  const calculateBMI = () => {
    const heightInMeters = height / 100;
    const bmi = (weight / (heightInMeters * heightInMeters)).toFixed(1);
    setBmiResult(bmi);

    if (bmi < 18.5) {
      setStatus("Underweight");
      setImageSrc("/Normal.png");
    } else if (bmi >= 18.5 && bmi <= 24.9) {
      setStatus("Normal weight");
      setImageSrc(
        "data:image/jpeg;base64,"
      );
    } else if (bmi >= 25 && bmi <= 29.9) {
      setStatus("Overweight");
      setImageSrc(
        "data:image/jpeg;base64,"
      ); 
    } else if (bmi >= 30 && bmi <= 34.9) {
      setStatus("Moderately obese)");
      setImageSrc(
        "data:image/jpeg;base64,"
      ); 
    } else if (bmi >= 35 && bmi <= 39.9) {
      setStatus("Severely obese");
      setImageSrc(
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQo1GiUZGH7bhTDP2TL8IMoNlbLWpEUwq2Usg&s"
      );
    } else if (bmi >= 40) {
      setStatus("Very severely obese");
      setImageSrc(
        "data:image/jpeg;base64,"
      ); 
    }
  };
  return (
    <div className="flex flex-col justify-center items-center h-screen">
      <div className="flex flex-col items-center gap-4">
        <div className="form-control w-full max-w-xs">
          <label className="label">
            <span className="label-text">Enter the Weight (kg)</span>
          </label>
          <input
            value={weight}
            onChange={(e) => setWeight(e.target.value)}
            placeholder="Enter the Weight"
            className="input input-bordered w-full max-w-xs"
          />
        </div>
        <div className="form-control w-full max-w-xs">
          <label className="label">
            <span className="label-text">Enter the Height (cm)</span>
          </label>
          <input
            value={height}
            onChange={(e) => setHeight(e.target.value)}
            placeholder="Enter the Height"
            className="input input-bordered w-full max-w-xs"
          />
        </div>
        <button onClick={calculateBMI} className="btn btn-primary mt-4">
          Calculate
        </button>

        {bmiResult && (
          <div id="results" className="mt-4 text-center">
            <p>
              Your BMI is: <strong>{bmiResult}</strong>
            </p>
            <p>
              Status: <strong>{status}</strong>
            </p>
            <img
              src={imageSrc}
              className="mt-4"
              style={{ width: "200px", height: "200px" }}
              alt="BMI Status"
            />
          </div>
        )}
      </div>
    </div>
  );
}

export default Size;
